import React, {useState} from 'react';
import {Container, Row, Col, ListGroup, ListGroupItem, FormControl} from 'react-bootstrap'
import {Switch, Route, Link, useParams, useHistory} from 'react-router-dom'
import './App.css';




function App() {
  return (
    <div className="App">
        <Switch>
          <Route path='/'>
            <SearchBar />
          </Route>
          <Route path='/client/:name'>
            <Clientpage />
          </Route>     
      </Switch>
    </div>
  );
}

export default App;



function SearchBar( props ){
  const history = useHistory()
  const [clients, setClients] = useState( () => {
    return require('./clients.json')
  })

  const [searchText, setSearchText] = useState('');

  return(
    <div>
      <Container>
        <Row>
          <Col>
            <h3 className='mb-3 mt-5'>
              <span className='text-white'>S</span>
              <span style={{color:"#6bd4c1"}}>earch</span> &nbsp;

              <span className='text-white'>U</span>
              <span style={{color:"#6bd4c1"}}>cms</span> &nbsp;

              <span className='text-white'>C</span>
              <span style={{color:"#6bd4c1"}}>lients</span>
            </h3>
              <FormControl
                size='lg' 
                type='text' 
                id='searchBox' 
                aria-label='search box' 
                placeholder='Search for client . . . ' 
                value={searchText} 
                onChange={e => setSearchText( e.target.value )} />

              <ListGroup style={{display: !searchText?'none':''}}>
                  {
                    clients.map(client => {

                      let name = client.firstName.toLowerCase();
                      let lastname = client.lastName.toLowerCase();

                      if( name.indexOf(searchText) !== -1){

                        // Listgroup bg colors: #1e1438  #142438

                        return <ListGroupItem key={client.email} className='shadow-lg' style={{backgroundColor:'#142438'}} onClick={() => history.push(`/client/${name}`, [client])}>

                              <h5 className='text-secondary'> 
                                <span style={{color:"#2d5d8e", fontWeight:'bold'}}>
                                      {client.firstName.toLowerCase()}
                                  </span> &nbsp;
                                  <span style={{color:"#6bd4c1"}}>
                                    {client.lastName.toLowerCase()}
                                  </span>

                              </h5>
                              <hr />
                              <p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Phone:</small> 
                                <small style={{color:"#6bd4c1"}}> {client.phoneNumber} </small>
                              </p>
                              <p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Email: </small> 
                                <small style={{color:"#6bd4c1"}}>{client.email}</small></p>
                              <p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Address: </small>
                                <small style={{color:"#6bd4c1"}}>{client.address}</small>
                              </p>

                          </ListGroupItem>
                      }else{
                        return <ListGroupItem style={{display:'none'}} key={client.email}>{client.firstName}</ListGroupItem>
                      }
                    })
                  }
              </ListGroup>
          </Col>
        </Row>
      </Container>

    </div>
  )
}

function Clientpage( props ) {
  const params = useParams();
  console.log(params)

  return (
    <div>
      <Container>
        <Row>
          <Col>
              <h1 style={{fontSize:30}}>Client Page</h1>
              <h1>{params.name}</h1>
          </Col>
        </Row>
      </Container>
    </div>
  )
}
