import React, {useState} from 'react';
import {Container, Row, Col, ListGroup, ListGroupItem, FormControl} from 'react-bootstrap'
import './App.css';


function SearchBar( props ){

  const [clients, setClients] = useState( () => {
    return require('./clients.json')
  })

  const [searchText, setSearchText] = useState('');

  console.log(clients)

  return(
    <div>
      <Container>
        <Row>
          <Col>
            <h3 className='mt-5'>Search</h3>
              <FormControl type='text' id='searchBox' aria-label='search box' placeholder='Search for client . . . '/>
              <ListGroup>
                  {
                    clients.map(client => {
                      let name = client.firstName.toLowerCase();
                      console.log('name', name)
                      if( name.indexOf(searchText) !== -1){
                        return <ListGroupItem>{client.firstName}</ListGroupItem>
                      }else{

                      }
                    })
                  }
              </ListGroup>
          </Col>
        </Row>
      </Container>

    </div>
  )
}



function App() {
  return (
    <div className="">
      <SearchBar />
    </div>
  );
}

export default App;
