import React, {useState, useEffect} from 'react';
import {Container, Row, Col, ListGroup, ListGroupItem, FormControl, Breadcrumb, InputGroup} from 'react-bootstrap'
import {Switch, Route, useParams, useHistory} from 'react-router-dom'
import styled from  'styled-components';
import './App.css';

import {UserSearch} from '@styled-icons/remix-line/UserSearch';
const UserSearchIcon = styled(UserSearch)`
  color: #6bd4c1 ;
  font-weight:bold;
  background-color: #142438;
  width: 50px; 

`;

function incrementLenVar() {
  let len = 0;

  function inc(){
    len++;
  }

  function getLen(){
    return len;
  }

  return {
    inc:inc,
    getLen:getLen
  }
}

function SearchBar( props ){
  const history = useHistory()
  const [clients, setClients] = useState( () => require('./clients.json'))

  const [searchText, setSearchText] = useState('');

function incrementLenVar() {
  let len = 0;

  function inc(){
    len++;
  }

  function getLen(){
    return len;
  }

  return {
    inc:inc,
    getLen:getLen
  }
}

  const increment = incrementLenVar();

useEffect(() => {

}, [increment.getLen()])


  return(
    <div>
      <Container>
      <Row>
          <Col>
            <h3 className='mb-5 pb-3 mt-5' style={{fontSize:'3em', borderBottom: '3px solid white'}}>
              <span className='text-white'>S</span>
              <span style={{color:"#6bd4c1"}}>earch</span>

              <i><span className='text-white'>U</span>
              <span style={{color:"#6bd4c1"}}>cms</span></i>

              <span className='text-white'>C</span>
              <span style={{color:"#6bd4c1"}}>lients</span>

              <i className='float-right' style={{color:"#6bd4c1"}}>Match Count: <span className='text-white' >{increment.getLen()}</span> </i>
            </h3>



              <div className='d-flex rounded' style={{backgroundColor:'#142438'}}>
                <UserSearchIcon size='36' className='mt-1'/>                  
                  <FormControl
                    className='shadow-lg'
                    style={{border:'none'}}
                    size='lg' 
                    type='text' 
                    id='searchBox'
                    placeholder='Search for client . . . '
                    onChange={e => setSearchText( e.target.value )} />         
              </div>

              <ListGroup style={{display: !searchText?'none':'', fontSize:'1.5em'}}>
                  {
                    !searchText
                        ? ""
                        : clients.map(client => {          

                      //convert client name to lowercase
                      let name = client.firstName.toLowerCase();
                      let lastname = client.lastName.toLowerCase();

                      //Build RegEx, search for first character match at the beginning of the string
                      let regexName = new RegExp(`^${searchText}`, 'ig')

                      if( name.match(regexName) ){
                        increment.inc()

                        console.log('Current Value:', increment.getLen())
                        return <ListGroupItem 
                                  key={client.email} 
                                  className='shadow-lg' 
                                  style={{backgroundColor:'#142438', cursor:'pointer', }} 
                                  onClick={() => history.push(`/client/${name}`, [client])}>


                              <h5 className='text-secondary' style={{fontSize:'1.5em'}}> 

                                  <span style={{color:"#2d5d8e", fontWeight:'bold'}}>
                                      {client.firstName.toLowerCase()}
                                  </span> &nbsp;

                                  <span style={{color:"#6bd4c1"}}>
                                    {client.lastName.toLowerCase()}
                                  </span>

                              </h5>                            
                          </ListGroupItem>
                      }else{
                        return <ListGroupItem style={{display:'none'}} key={client.email}>{client.firstName}</ListGroupItem>
                      }
                    })
                  }
              </ListGroup>
          </Col>
        </Row>
      </Container>

    </div>
  )
}

function Clientpage( props ) {
    const params = useParams();
    const history = useHistory()
    const [client, setClient] = useState([])

    useEffect(() => {
      if(history.location.state){
        setClient(history.location.state[0])
      }else{
        history.push('/')
      }
    })


  return (
    <div>
      <Container>
        <Row id='client-container'>     
          <Col className='text-white text-cente align-self-center rounded shadow-lg' style={{ backgroundColor: '#142438', border:'3px solid #6bd4c1'}}>


              <div className='p-5'>
              <Breadcrumb className='breadcrumb' style={{backgroundColor:'#142438'}}>
                <Breadcrumb.Item onClick={() => history.push('/')}>Home</Breadcrumb.Item>
                <Breadcrumb.Item active>Client</Breadcrumb.Item>
              </Breadcrumb>

                <h1 style={{fontSize:'3em'}}>{client.firstName} {client.lastName}</h1> <hr style={{backgroundColor:"#6bd4c1"}}/>
                <section style={{fontSize:'2em'}}>
                  <p>
                      <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Phone:</small> 
                      <small style={{color:"#6bd4c1"}}> {client.phoneNumber} </small>
                  </p>
                  <p>
                    <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Email: </small> 
                    <small style={{color:"#6bd4c1"}}>{client.email}</small></p>
                  <p>
                    <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Address: </small>
                    <small style={{color:"#6bd4c1"}}>{client.address}</small>
                  </p>
                  <p>

                    <small>
                        <span style={{fontWeight:'bold', color:"#2d5d8e", marginRight:3}}> City: </span> {client.city} &nbsp;
                        <span style={{fontWeight:'bold', color:"#2d5d8e"}}> State: </span> {client.state} &nbsp;
                        <span style={{fontWeight:'bold', color:"#2d5d8e"}}> Zipcode: </span> {client.zipcode}
                    </small>
                  </p>
                </section>
              </div>
          </Col>
        </Row>
      </Container>
    </div>
  )
}


function App() {
  return (
    <div className="App">
        <Switch>
          <Route exact path='/'>
            <SearchBar />
          </Route>
          <Route path='/client/:name'>
            <Clientpage />
          </Route>     
      </Switch>
    </div>
  );
}

export default App;
