import React, {useState, useEffect} from 'react';
import {Container, Row, Col, ListGroup, ListGroupItem, FormControl, Breadcrumb} from 'react-bootstrap'
import {Switch, Route, useParams, useHistory} from 'react-router-dom'
import styled from  'styled-components';
import './App.css';

import {UserSearch} from '@styled-icons/remix-line/UserSearch';
const UserSearchIcon = styled(UserSearch)`color: grey`

function SearchBar( props ){
  const history = useHistory()
  const [clients, setClients] = useState( () => require('./clients.json'))

  const [searchText, setSearchText] = useState('');

  return(
    <div>
      <Container>
      <Row>
          <Col>
            <h3 className='mb-3 mt-5' style={{fontSize:'3em'}}>
              <span className='text-white'>S</span>
              <span style={{color:"#6bd4c1"}}>earch</span>

              <i><span className='text-white'>U</span>
              <span style={{color:"#6bd4c1"}}>cms</span></i>

              <span className='text-white'>C</span>
              <span style={{color:"#6bd4c1"}}>lients</span>
            </h3>
            <hr className='mb-4' style={{backgroundColor:'white'}} />

              <div className=''>

                {/* <UserSearchIcon size='36' className=''/> */}
                <FormControl
                  className='shadow'
                  size='lg' 
                  type='text' 
                  id='searchBox'
                  placeholder='Search for client . . . '
                  onChange={e => setSearchText( e.target.value )} />
              </div>

              <ListGroup style={{display: !searchText?'none':'', fontSize:'1.5em'}}>
                  {
                    clients.map(client => {

                      let name = client.firstName.toLowerCase();
                      let lastname = client.lastName.toLowerCase();

                      if( name.indexOf(searchText) !== -1){

                        // Listgroup bg colors: #1e1438  #142438

                        return <ListGroupItem 
                                  key={client.email} 
                                  className='shadow-lg' 
                                  style={{backgroundColor:'#142438', cursor:'pointer'}} 
                                  onClick={() => history.push(`/client/${name}`, [client])}>

<hr style={{ backgroundColor:'#6bd4c1'}}/>

                              <h5 className='text-secondary' style={{fontSize:'1.5em'}}> 
                                  <span style={{color:"#2d5d8e", fontWeight:'bold'}}>
                                      {client.firstName.toLowerCase()}
                                  </span> &nbsp;
                                  <span style={{color:"#6bd4c1"}}>
                                    {client.lastName.toLowerCase()}
                                  </span>

                              </h5>
                              {/* <hr style={{ backgroundColor:'#6bd4c1'}}/> */}
                              <div className='text-center' ><p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Phone:</small> 
                                <small style={{color:"#6bd4c1"}}> {client.phoneNumber} </small>
                              </p>
                              <p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Email: </small> 
                                <small style={{color:"#6bd4c1"}}>{client.email}</small></p>
                              <p>
                                <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Address: </small>
                                <small style={{color:"#6bd4c1"}}>{client.address}</small>
                              </p></div>

                          </ListGroupItem>
                      }else{
                        return <ListGroupItem style={{display:'none'}} key={client.email}>{client.firstName}</ListGroupItem>
                      }
                    })
                  }
              </ListGroup>
          </Col>
        </Row>
      </Container>

    </div>
  )
}

function Clientpage( props ) {
  const params = useParams();
  const history = useHistory()
  const [client, setClient] = useState([])
console.log(history)
  useEffect(() => {
    if(history.location.state){
      setClient(history.location.state[0])
    }else{
      history.push('/')
    }
  })


  return (
    <div>
      <Container>
        <Row id='client-container'>     
          <Col className='text-white text-center bg-dark align-self-center rounded shadow'>

              <div className='p-5'>
              <Breadcrumb className='breadcrumb bg-dark'>
                <Breadcrumb.Item onClick={() => history.push('/')}>Home</Breadcrumb.Item>
                <Breadcrumb.Item active>Client</Breadcrumb.Item>
              </Breadcrumb>

                <h1>{client.firstName} {client.lastName}</h1> <hr style={{backgroundColor:"#6bd4c1"}}/>
                <p>
                    <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Phone:</small> 
                    <small style={{color:"#6bd4c1"}}> {client.phoneNumber} </small>
                </p>
                <p>
                  <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Email: </small> 
                  <small style={{color:"#6bd4c1"}}>{client.email}</small></p>
                <p>
                  <small style={{color:"#2d5d8e", fontWeight:'bold'}}>Address: </small>
                  <small style={{color:"#6bd4c1"}}>{client.address}</small>
                </p>
              </div>
          </Col>
        </Row>
      </Container>
    </div>
  )
}


function App() {
  return (
    <div className="App">
        <Switch>
          <Route exact path='/'>
            <SearchBar />
          </Route>
          <Route path='/client/:name'>
            <Clientpage />
          </Route>     
      </Switch>
    </div>
  );
}

export default App;
