import React, {useState} from 'react';
import {Container, Row, Col, ListGroup, ListGroupItem, FormControl} from 'react-bootstrap'
import './App.css';


function SearchBar( props ){

  const [clients, setClients] = useState( () => {
    return require('./clients.json')
  })

  const [searchText, setSearchText] = useState('');

  return(
    <div>
      <Container>
        <Row>
          <Col>
            <h3 className='mt-5'><span className='text-white'>S</span><span style={{color:"#6bd4c1"}}>earch</span> </h3>
              <FormControl 
                type='text' 
                id='searchBox' 
                aria-label='search box' 
                placeholder='Search for client . . . ' 
                value={searchText} 
                onChange={e => setSearchText( e.target.value )} />

              <ListGroup style={{display: !searchText?'none':''}}>
                  {
                    clients.map(client => {

                      let name = client.firstName.toLowerCase();

                      if( name.indexOf(searchText) !== -1){
                        return <ListGroupItem key={client.email}>
                            <h5 className='text-secondary'> <span style={{color:'#2d5d8e', fontWeight:'bold'}}>{client.firstName.toLowerCase()}</span> {client.lastName.toLowerCase()}</h5>
                            <hr />
                            <p><small>Phone: {client.phoneNumber}</small></p>
                            <p><small>Email: {client.email}</small></p>
                            <p><small>Address: {client.address}</small></p>

                          </ListGroupItem>
                      }else{
                        return <ListGroupItem style={{display:'none'}} key={client.email}>{client.firstName}</ListGroupItem>
                      }
                    })
                  }
              </ListGroup>
          </Col>
        </Row>
      </Container>

    </div>
  )
}



function App() {
  return (
    <div className="App">
      <SearchBar />
    </div>
  );
}

export default App;
